function SlideUp(props) {
	const hideOPen = () => {
		props.setTrigger(false);
		document.modalWrap.className=''
	}
  return (
    <div>
       <div className={props.trigger ? "modalWrap show" : "modalWrap"} id="modalDefault" tabIndex="0">
			<div className="modalDefault fix_bottom">
				<div className="header">
					<h1 className="headerTitle">이용안내</h1>
					<button type="button" className="btn closeBtn" id="defaultClose" onClick={hideOPen}>닫기</button>
				</div>
				<div data-role="content" className="modal-contents">
					<section className="with-fixedBtn">
						<p className="centeredTxt">NH간편오토론 대출은 운전면허증 소지자로<br />신차 구입자금 용도로만 대출신청이 가능합니다.</p>
					</section>
					<button type="submit" className="btn primary submit selfBottom" onClick={hideOPen}>확인</button>
				</div>
			</div>
			<div className={props.trigger ? "dimmed" : ""}></div>
		{/* <span className="helper_a11y" tabIndex="0"></span> */}
		</div>
    </div> 
  )
}

export default SlideUp;
